-- Name: first_listing_date(bigint); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.first_listing_date(dno bigint) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    x INT;
    y VARCHAR(50);
    z VARCHAR(50);
    c VARCHAR(50);
BEGIN
 SELECT diary_no, 
       TO_CHAR(MIN(nextdt), 'DD-MM-YYYY') AS nextdt, 
       MAX(board_type) AS board_type,  -- Use aggregate function for non-aggregated columns
       MAX(judges) AS judges            -- Use aggregate function for non-aggregated columns
INTO x, y, z, c
FROM (
    SELECT diary_no, 
           next_dt AS nextdt, 
           board_type, 
           judges 
    FROM heardt 
    WHERE diary_no =dno
      AND (main_supp_flag = 1 OR main_supp_flag = 2) 
      AND next_dt <= CURRENT_DATE
    
    UNION ALL  -- Use UNION ALL to include all rows if duplicates are not a concern
    
    SELECT diary_no, 
           next_dt AS nextdt, 
           board_type, 
           judges 
    FROM last_heardt 
    WHERE diary_no = dno 
      AND next_dt <= CURRENT_DATE 
      AND (main_supp_flag = 1 OR main_supp_flag = 2) 
      AND (bench_flag IS NULL OR bench_flag = '')
) a group by a.diary_no;
    RETURN y;
END;
$$;


