    x VARCHAR(50);
    y VARCHAR(50);
    z VARCHAR(50);
    d VARCHAR(50);
    e VARCHAR(50);
    f VARCHAR(50);
BEGIN
    -- First UNION query
    SELECT us.section_name, us.id AS sec_id, u.empid, u.name
    INTO x, y, z
