  JOIN master.users u ON b.dacode = u.usercode AND u.display = 'Y'
  JOIN master.usersection us ON u.section = us.id AND us.display = 'Y'
  WHERE b.display = 'Y'
  GROUP BY a.diary_no, sec_id 
 
  
  union
  
     SELECT 
  substring(string_agg(us.section_name, ',' ORDER BY e_date), 1, position(',' in string_agg(us.section_name, ',' ORDER BY e_date))-1) AS section_name,
  us.id AS sec_id,
